import{_ as l,c as i,a3 as e,o as r}from"./chunks/framework.CAwIQbWu.js";const t="/assets/%E8%AE%A1%E7%AE%97%E6%9C%BA%E5%8F%91%E5%B1%95%E5%8E%86%E5%8F%B2.BIR-Rtnk.png",b=JSON.parse('{"title":"计算机的起源","description":"","frontmatter":{},"headers":[],"relativePath":"CSBasic/计算机的起源.md","filePath":"CSBasic/计算机的起源.md","lastUpdated":1677122299000}'),o={name:"CSBasic/计算机的起源.md"};function h(n,a,d,s,p,u){return r(),i("div",null,a[0]||(a[0]=[e('<h1 id="计算机的起源" tabindex="-1">计算机的起源 <a class="header-anchor" href="#计算机的起源" aria-label="Permalink to &quot;计算机的起源&quot;">​</a></h1><p>本章分六部分，计算机的发展历程，计算机的分类，计算机的体系结构，计算机的层次与编程语言，计算机的计算单位，计算机的字符与编码集</p><h2 id="计算机的发展简史" tabindex="-1">计算机的发展简史 <a class="header-anchor" href="#计算机的发展简史" aria-label="Permalink to &quot;计算机的发展简史&quot;">​</a></h2><h3 id="计算机发展的四个阶段" tabindex="-1">计算机发展的四个阶段 <a class="header-anchor" href="#计算机发展的四个阶段" aria-label="Permalink to &quot;计算机发展的四个阶段&quot;">​</a></h3><p>​ 1946——1957 电子管计算机</p><p>​ 1957——1964 晶体管计算机</p><p>​ 1964——1980 集成电路计算机</p><p>​ 1980——至今 超大集成电路计算机</p><p>​ <img src="'+t+'" alt="计算机发展历史"></p><h4 id="第一阶段-电子管计算机" tabindex="-1">第一阶段：电子管计算机 <a class="header-anchor" href="#第一阶段-电子管计算机" aria-label="Permalink to &quot;第一阶段：电子管计算机&quot;">​</a></h4><ul><li>第二次世界大战是电子管计算机产生的催化剂</li><li>埃尼阿克（ENIAC） <ul><li>产生原因： <ul><li>战争使用了飞机和火箭</li><li>打得准则需要计算射击参数</li><li>射击参数需要几千次运算才能计算出来</li></ul></li><li>特点： <ul><li>18000 多个电子管</li><li>运行耗电量 150 千瓦</li><li>重量达 30 吨，占地 1500 平方英尺</li></ul></li></ul></li><li>电子管计算机的特点： <ul><li>集成度小，空间占用大</li><li>功耗搞，运行速度慢</li><li>操作复杂，更换程序需要接线</li></ul></li></ul><h4 id="第二阶段-晶体管计算机" tabindex="-1">第二阶段：晶体管计算机 <a class="header-anchor" href="#第二阶段-晶体管计算机" aria-label="Permalink to &quot;第二阶段：晶体管计算机&quot;">​</a></h4><p>起因：贝尔实验室的三个科学家发明了晶体管</p><p>特点：</p><ul><li>集成度相对较高，空间占用相对小</li><li>功耗相对较低，运行速度较快</li><li>操作相对简单，交互更加方便</li></ul><h4 id="第三阶段-集成电路计算机" tabindex="-1">第三阶段：集成电路计算机 <a class="header-anchor" href="#第三阶段-集成电路计算机" aria-label="Permalink to &quot;第三阶段：集成电路计算机&quot;">​</a></h4><p>起因：德州仪器的工程师发明了集成电路（IC）</p><p>特点：</p><ul><li>计算机变得更小</li><li>功耗变得更低</li><li>计算速度变得更快</li></ul><h4 id="第四阶段-超大规模集成电路计算机" tabindex="-1">第四阶段：超大规模集成电路计算机 <a class="header-anchor" href="#第四阶段-超大规模集成电路计算机" aria-label="Permalink to &quot;第四阶段：超大规模集成电路计算机&quot;">​</a></h4><ul><li>一个芯片集成了上百万的晶体管</li><li>速度更快，体积更小，价格更低</li><li>用途更广：应用上升</li></ul><h4 id="第五阶段-未来的计算机" tabindex="-1">第五阶段：未来的计算机 <a class="header-anchor" href="#第五阶段-未来的计算机" aria-label="Permalink to &quot;第五阶段：未来的计算机&quot;">​</a></h4><p>生物计算机：以蛋白质分子作为主要原材料</p><p>量子计算机：遵循量子力学的物理计算机</p><h3 id="微型计算机的发展历程" tabindex="-1">微型计算机的发展历程 <a class="header-anchor" href="#微型计算机的发展历程" aria-label="Permalink to &quot;微型计算机的发展历程&quot;">​</a></h3><p>受限于性能</p><p>摩尔定律：集成电路的性能，每 18—24 个月就会提升一倍</p><p>单核 CPU —— 多核 CPU</p><h2 id="计算机的分类" tabindex="-1">计算机的分类 <a class="header-anchor" href="#计算机的分类" aria-label="Permalink to &quot;计算机的分类&quot;">​</a></h2><h2 id="计算机的体系结构" tabindex="-1">计算机的体系结构 <a class="header-anchor" href="#计算机的体系结构" aria-label="Permalink to &quot;计算机的体系结构&quot;">​</a></h2><h2 id="计算机的层次与编程语言" tabindex="-1">计算机的层次与编程语言 <a class="header-anchor" href="#计算机的层次与编程语言" aria-label="Permalink to &quot;计算机的层次与编程语言&quot;">​</a></h2><h2 id="计算机的计算单位" tabindex="-1">计算机的计算单位 <a class="header-anchor" href="#计算机的计算单位" aria-label="Permalink to &quot;计算机的计算单位&quot;">​</a></h2><h3 id="计算机的字符与编码集" tabindex="-1">计算机的字符与编码集 <a class="header-anchor" href="#计算机的字符与编码集" aria-label="Permalink to &quot;计算机的字符与编码集&quot;">​</a></h3><h4 id="电子管计算机" tabindex="-1">电子管计算机 <a class="header-anchor" href="#电子管计算机" aria-label="Permalink to &quot;电子管计算机&quot;">​</a></h4><p>​ 二极管</p><p>​ 第二次世界大战是电子管计算机产生的催化剂</p><p>​ 英国为了解密德国海军的密文</p><p>​ ps:我觉得可以看看卷福演的《模仿游戏》，讲的就是这件事情，图灵的伟大</p><p>​ 电子管计算机——埃尼阿克（ENIAC）,18000 多个电子管，运行耗电量 150 千瓦，重达 30 村，占地 1500 平方英尺</p>',39)]))}const q=l(o,[["render",h]]);export{b as __pageData,q as default};
