import{_ as l,c as e,a3 as t,o}from"./chunks/framework.CAwIQbWu.js";const d=JSON.parse('{"title":"扣证篇","description":"","frontmatter":{},"headers":[],"relativePath":"SubjectOne/常识/扣证篇.md","filePath":"SubjectOne/常识/扣证篇.md","lastUpdated":1752144059000}'),r={name:"SubjectOne/常识/扣证篇.md"};function i(n,a,s,u,p,c){return o(),e("div",null,a[0]||(a[0]=[t('<h1 id="扣证篇" tabindex="-1">扣证篇 <a class="header-anchor" href="#扣证篇" aria-label="Permalink to &quot;扣证篇&quot;">​</a></h1><h2 id="_1-一个记分周期内记满12分" tabindex="-1">1. 一个记分周期内记满12分 <a class="header-anchor" href="#_1-一个记分周期内记满12分" aria-label="Permalink to &quot;1. 一个记分周期内记满12分&quot;">​</a></h2><ul><li><strong>扣留驾驶证</strong></li></ul><blockquote><p>一个记分周期内记满12分，交警可依法扣留驾驶证。驾驶人要重新学习并通过考试才能拿回驾驶证。</p></blockquote><blockquote><p>记分满12分的，机动车驾驶证将被扣留，并由交通管理部门开具强制措施凭证，送达满分教育通知书，通知机动车驾驶人参加满分学习、考试。</p></blockquote><ul><li>不能再处理其他机动车的违法记录</li></ul><blockquote><p>机动车驾驶人可以一次性处理同一辆机动车的多起交通违法行为记录，若未记满12分，还可以处理其他的车辆违法记录，若记满12分，则不能再处理其他机动车的违法记录。</p></blockquote><ul><li><strong>有临时选临时</strong></li></ul><blockquote><p><code>临时入境的</code>机动车驾驶人，在一个记分周期内记满12分的，注销临时机动车驾驶许可。</p></blockquote><h2 id="_2-将车交与无驾驶证或被吊销的人驾驶" tabindex="-1">2. 将车交与无驾驶证或被吊销的人驾驶 <a class="header-anchor" href="#_2-将车交与无驾驶证或被吊销的人驾驶" aria-label="Permalink to &quot;2. 将车交与无驾驶证或被吊销的人驾驶&quot;">​</a></h2><ul><li><p><strong>扣留驾驶证</strong></p></li><li><p>罚款200-2000元</p></li></ul><blockquote><p>将机动车交给驾驶证被吊销的人驾驶，会追究连带责任，交警可依法<strong>扣留</strong>车主驾驶证，并处<strong>罚款</strong>200-2000元。</p></blockquote><h2 id="_3-超速50" tabindex="-1">3. 超速50% <a class="header-anchor" href="#_3-超速50" aria-label="Permalink to &quot;3. 超速50%&quot;">​</a></h2><ul><li><p>记12分</p></li><li><p>罚款200-2000元</p></li><li><p><strong>吊销驾驶证</strong></p></li></ul><h2 id="_4-初次饮酒后驾车" tabindex="-1">4. 初次饮酒后驾车 <a class="header-anchor" href="#_4-初次饮酒后驾车" aria-label="Permalink to &quot;4. 初次饮酒后驾车&quot;">​</a></h2><ul><li><p><strong>暂扣驾驶证6个月</strong></p></li><li><p>罚款1000-2000元</p></li></ul><h2 id="_5-再次饮酒后驾车" tabindex="-1">5. 再次饮酒后驾车 <a class="header-anchor" href="#_5-再次饮酒后驾车" aria-label="Permalink to &quot;5. 再次饮酒后驾车&quot;">​</a></h2><ul><li><p>处10日以下拘留</p></li><li><p>罚款1000-2000元</p></li><li><p><strong>吊销驾驶证</strong></p></li></ul><h2 id="_6-驾驶拼装机动车上路" tabindex="-1">6. 驾驶拼装机动车上路 <a class="header-anchor" href="#_6-驾驶拼装机动车上路" aria-label="Permalink to &quot;6. 驾驶拼装机动车上路&quot;">​</a></h2><ul><li><p>罚款200-2000元</p></li><li><p><strong>吊销驾驶证</strong></p></li><li><p>收缴车辆，强制报废</p></li></ul><h2 id="_7-犯罪扣证不扣分" tabindex="-1">7. 犯罪扣证不扣分 <a class="header-anchor" href="#_7-犯罪扣证不扣分" aria-label="Permalink to &quot;7. 犯罪扣证不扣分&quot;">​</a></h2><ul><li><strong>吊销驾驶证</strong></li></ul><blockquote><p>肇事逃逸致人死亡且构成犯罪的，属于违法行为，会被吊销驾驶证，而不是记12分。</p></blockquote><h2 id="_8-实习扣证不扣分" tabindex="-1">8. 实习扣证不扣分 <a class="header-anchor" href="#_8-实习扣证不扣分" aria-label="Permalink to &quot;8. 实习扣证不扣分&quot;">​</a></h2>',24)]))}const b=l(r,[["render",i]]);export{d as __pageData,b as default};
