import{_ as e,c as l,a3 as a,o as t}from"./chunks/framework.CAwIQbWu.js";const q=JSON.parse('{"title":"扣车篇","description":"","frontmatter":{},"headers":[],"relativePath":"SubjectOne/常识/扣车篇.md","filePath":"SubjectOne/常识/扣车篇.md","lastUpdated":1752144059000}'),u={name:"SubjectOne/常识/扣车篇.md"};function i(c,o,r,n,b,d){return t(),l("div",null,o[0]||(o[0]=[a('<h1 id="扣车篇" tabindex="-1">扣车篇 <a class="header-anchor" href="#扣车篇" aria-label="Permalink to &quot;扣车篇&quot;">​</a></h1><h2 id="_1-发生道路交通事故需要收集证据" tabindex="-1">1. 发生道路交通事故需要收集证据 <a class="header-anchor" href="#_1-发生道路交通事故需要收集证据" aria-label="Permalink to &quot;1. 发生道路交通事故需要收集证据&quot;">​</a></h2><blockquote><p>扣留发生交通事故的事故车是为了收集证据，更好地解决事故问题需要，驾驶人要积极配合。</p></blockquote><h2 id="_2-号牌问题" tabindex="-1">2. 号牌问题 <a class="header-anchor" href="#_2-号牌问题" aria-label="Permalink to &quot;2. 号牌问题&quot;">​</a></h2><ul><li>无号牌</li><li>遮挡号牌</li><li>虚假号牌</li></ul><blockquote><p>有使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志嫌疑的，交警可依法扣留车辆。</p></blockquote><blockquote><p>伪造、变造或使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处15日以下拘留，并处罚款2000-5000元。</p></blockquote><h2 id="_3-检验合格标志问题" tabindex="-1">3. 检验合格标志问题 <a class="header-anchor" href="#_3-检验合格标志问题" aria-label="Permalink to &quot;3. 检验合格标志问题&quot;">​</a></h2><ul><li>无检验合格标志</li><li>虚假检验合格标志</li></ul><blockquote><p>有使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志嫌疑的，交警可依法扣留车辆。</p></blockquote><h2 id="_4-保险标志问题" tabindex="-1">4. 保险标志问题 <a class="header-anchor" href="#_4-保险标志问题" aria-label="Permalink to &quot;4. 保险标志问题&quot;">​</a></h2><ul><li>无保险标志/未按照国家规定投保<strong>交强险</strong></li></ul><blockquote><p>未按规定投保，可依法<code>扣留车辆</code>直至投保后，并处保险费的<code>2</code>倍罚款。</p></blockquote><ul><li><p><strong>扣留车辆</strong></p></li><li><p><strong>处2倍保险费（投保最低限额）罚款</strong></p></li></ul><blockquote><p>未投保交强险的车辆，除了会被<code>扣留车辆</code>，还要按投保最低限额<code>2</code>倍保险费罚款。</p></blockquote><h2 id="_5-两证问题" tabindex="-1">5. 两证问题 <a class="header-anchor" href="#_5-两证问题" aria-label="Permalink to &quot;5. 两证问题&quot;">​</a></h2><ul><li>未随车携带行驶证及驾驶证</li></ul><blockquote><p>机动车上路行驶，必须悬挂号牌，放置检验合格标志、保险标志，随车携带行驶证及驾驶证，否则交警可依法扣留车辆。</p></blockquote><blockquote><p>开车上路不带驾驶证、行驶证，除扣留机动车外，还将处罚款20-200元。</p></blockquote><h2 id="_6-违法问题" tabindex="-1">6. 违法问题 <a class="header-anchor" href="#_6-违法问题" aria-label="Permalink to &quot;6. 违法问题&quot;">​</a></h2><blockquote><p>拒绝接受罚款处罚的，依法扣留其非机动车。</p></blockquote><ul><li>闯红灯</li></ul><blockquote><p>驾驶非机动车闯红灯，非机动车不按非机动车信号灯表示通行的处50元罚款</p></blockquote><ul><li>违法载人</li></ul><blockquote><p>驾驶电动自行车违法载人的，处警告或5元以上50元以下罚款</p></blockquote><ul><li>不接受处理</li></ul><blockquote><p>公安机关交通管理部门扣留机动车、非机动车，当事人逾期不接受处理，且经公告3个月仍不接受处理的，可对扣留的车辆依法处理。</p></blockquote><ul><li>无牌无证</li></ul><blockquote><p>驾驶无牌无证的电动自行车、残疾人机动轮椅车，或者驾驶本市禁止通行的非机动车上道路行驶的，处罚款50-200元；不能提供来源合法凭证的，予以暂扣或没收。</p></blockquote><h2 id="♥" tabindex="-1">♥ <a class="header-anchor" href="#♥" aria-label="Permalink to &quot;♥&quot;">​</a></h2><ul><li>车辆被扣留时，当事人可自行处理车辆所载货物。</li></ul><blockquote><p>公安机关交通管理部门扣留车辆的，不得扣留车辆所载货物。</p></blockquote>',32)]))}const h=e(u,[["render",i]]);export{q as __pageData,h as default};
