import{_ as o,c as l,a3 as a,o as t}from"./chunks/framework.CAwIQbWu.js";const s=JSON.parse('{"title":"标志篇","description":"","frontmatter":{},"headers":[],"relativePath":"SubjectOne/常识/标志篇.md","filePath":"SubjectOne/常识/标志篇.md","lastUpdated":1752317142000}'),p={name:"SubjectOne/常识/标志篇.md"};function i(r,e,c,u,d,b){return t(),l("div",null,e[0]||(e[0]=[a('<h1 id="标志篇" tabindex="-1">标志篇 <a class="header-anchor" href="#标志篇" aria-label="Permalink to &quot;标志篇&quot;">​</a></h1><blockquote><p>红色-禁令（禁令标志表示禁止、限制及相应解除的含义，道路使用者应严格遵守）</p></blockquote><blockquote><p>蓝色/绿色-指示（指示标志表示指示车辆、行人行进的含义，道路使用者应遵守）</p></blockquote><blockquote><p>黄色-警告（警告标志警告车辆驾驶人应注意前方有难以发现的情况、需减速慢行或采取其他安全行动的情况）</p></blockquote><blockquote><p>黑色-辅助（辅助标志应安装在主标志下面，紧靠主标志下缘）</p></blockquote><blockquote><p>咖啡色-旅游区（用以指引人们前往邻近的旅游区，识别通往旅游区的方向和距离，了解旅游项目的类别。）</p></blockquote><blockquote><p>指路标志（指路标志表示道路信息的指引，为驾驶人提供去往目的地所经过的道路、沿途相关城镇、重要公共设施、服务设施、地点、距离和行车方向等信息。）</p></blockquote><blockquote><p>告示标志（用以解释道路设施、指引路外设施或告示有关道路交通安全法规及交通管理安全行车的提醒等内容。）</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732598192326_%E8%BF%87%E6%B0%B4%E8%B7%AF%E9%9D%A2.png" alt="过水路面.png"></p><h2 id="_1-过水路面警告标志" tabindex="-1">1. 过水路面警告标志 <a class="header-anchor" href="#_1-过水路面警告标志" aria-label="Permalink to &quot;1. 过水路面警告标志&quot;">​</a></h2><ul><li>logo</li></ul><blockquote><p>黄牌标志内车辆驶过，水花溅起。</p></blockquote><ul><li>低速通过</li></ul><blockquote><p>行经漫水路或漫水桥，应停车察明水情，确认安全再低速通过。</p></blockquote><blockquote><p>行经两侧有行人且有积水的路面，应减速慢行，<strong>避免积水溅到行人</strong>。</p></blockquote><blockquote><p>行经两侧有非机动车行驶且有积水的路面，应减速慢行，<strong>避免积水溅到非机动车驾驶人</strong>。</p></blockquote><ul><li>加大安全距离</li></ul><blockquote><p>涉水路段路况复杂，为安全起见，应适当加大安全车距。</p></blockquote><ul><li>挂低速挡</li></ul><blockquote><p>过漫水路，为防止水进入发动机，应<strong>挂低速挡</strong>通过。</p></blockquote><blockquote><p>行经漫水路段，应减速慢行，<strong>不得空挡滑行</strong>。</p></blockquote><blockquote><p>通过漫水路时，要挂低速挡匀速通过，以免中途熄火，<strong>用高挡位会熄火</strong>。</p></blockquote><ul><li>间断轻踏制动踏板</li></ul><blockquote><p>车辆涉水后，刹车片可能进水，制动效果会变差，可通过间断轻踏蒸发水分，以恢复制动。</p></blockquote><ul><li>（<strong>发生“水滑”时</strong>）慢抬油门，慢踩刹车</li></ul><blockquote><p>发生水滑时，不能急踩制动踏板，不能迅速转向，要慢抬油门，慢踩刹车。</p></blockquote><blockquote><p>避免“水滑”最好的办法就是降低车速。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732629660203_%E5%87%8F%E9%80%9F%E8%AE%A9%E8%A1%8C.png" alt="减速让行.png"></p><h2 id="_2-减速让行" tabindex="-1">2. 减速让行 <a class="header-anchor" href="#_2-减速让行" aria-label="Permalink to &quot;2. 减速让行&quot;">​</a></h2><ul><li>在路口减速观察，确认安全后通过</li></ul><blockquote><p>图中倒三角里面一个“让”字，是减速让行标志，需要减速让行。 三角减速，八角停车</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732637393461_CN_road_sign_%E8%AD%A6_1-9.svg" alt="CN_road_sign_警_1-9.svg"></p><h2 id="_3-前方左侧有岔路" tabindex="-1">3. 前方左侧有岔路 <a class="header-anchor" href="#_3-前方左侧有岔路" aria-label="Permalink to &quot;3. 前方左侧有岔路&quot;">​</a></h2><ul><li>前方路口不能右转</li></ul><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732804415978_%E9%81%BF%E9%99%A9%E8%BD%A6%E9%81%93.gif" alt="避险车道.gif"></p><h2 id="_4-避险车道" tabindex="-1">4. 避险车道 <a class="header-anchor" href="#_4-避险车道" aria-label="Permalink to &quot;4. 避险车道&quot;">​</a></h2><ul><li>禁止临时停车</li><li>一般设置在长下坡路段</li><li>一般设有预告标志</li></ul><blockquote><p><code>制动失效</code>后，应尽快找到紧急避险车道，停车后拉手刹，以防溜车。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732807687168_%E7%94%B5%E5%8A%A8%E8%87%AA%E8%A1%8C%E8%BD%A6%E8%A1%8C%E9%A9%B6.png" alt="电动自行车行驶.png"></p><blockquote><p>方形车道，圆形行驶</p></blockquote><h2 id="_5-电动自行车行驶" tabindex="-1">5. 电动自行车行驶 <a class="header-anchor" href="#_5-电动自行车行驶" aria-label="Permalink to &quot;5. 电动自行车行驶&quot;">​</a></h2><ul><li>logo</li></ul><blockquote><p>圆形</p></blockquote><h2 id="_6-按限速标志行驶" tabindex="-1">6. 按限速标志行驶 <a class="header-anchor" href="#_6-按限速标志行驶" aria-label="Permalink to &quot;6. 按限速标志行驶&quot;">​</a></h2><blockquote><p>在高速公路上行车，应要严格按道路限速标志标明的车速行驶，而不是按照车载导航指示的车速行驶。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732811412071_%E5%8D%95%E8%A1%8C%E8%B7%AF%EF%BC%88%E7%9B%B4%E8%A1%8C%EF%BC%89.png" alt="单行路（直行）.png"></p><h2 id="_7-单行路-直行" tabindex="-1">7. 单行路（直行） <a class="header-anchor" href="#_7-单行路-直行" aria-label="Permalink to &quot;7. 单行路（直行）&quot;">​</a></h2><ul><li>logo</li></ul><blockquote><p>长方形蓝底两边没有虚线，代表单行路。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732811990654_%E5%89%8D%E6%96%B9%E7%9B%B4%E8%A1%8C.png" alt="前方直行.png"></p><h2 id="_8-前方直行" tabindex="-1">8. 前方直行 <a class="header-anchor" href="#_8-前方直行" aria-label="Permalink to &quot;8. 前方直行&quot;">​</a></h2><ul><li>logo</li></ul><blockquote><p>圆形蓝底代表前方直行。表示一切车辆只准直行。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732812017335_%E7%9B%B4%E8%A1%8C%E8%BD%A6%E9%81%93.svg" alt="直行车道.svg"></p><h2 id="_9-直行车道" tabindex="-1">9. 直行车道 <a class="header-anchor" href="#_9-直行车道" aria-label="Permalink to &quot;9. 直行车道&quot;">​</a></h2><ul><li>logo</li></ul><blockquote><p>长方形略宽，两边有虚线，代表直行车道。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732811996529_%E5%B9%B2%E8%B7%AF%E5%85%88%E8%A1%8C.svg" alt="干路先行.svg"></p><h2 id="_10-干路先行" tabindex="-1">10. 干路先行 <a class="header-anchor" href="#_10-干路先行" aria-label="Permalink to &quot;10. 干路先行&quot;">​</a></h2><ul><li>logo</li></ul><blockquote><p>蓝底一个小箭头略粗，中间一条短横线，代表干路先行。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732862317817_%E7%A1%AC%E8%B7%AF%E8%82%A9%E5%85%81%E8%AE%B8%E8%A1%8C%E9%A9%B6%E7%BB%88%E7%82%B9.png" alt="硬路肩允许行驶终点.png"></p><h2 id="_11-硬路肩允许行驶终点" tabindex="-1">11. 硬路肩允许行驶终点 <a class="header-anchor" href="#_11-硬路肩允许行驶终点" aria-label="Permalink to &quot;11. 硬路肩允许行驶终点&quot;">​</a></h2><ul><li>logo</li></ul><blockquote><p>白线右侧代表硬路肩，直行箭头上有红斜杠代表硬路肩允许行驶路段结束。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732899809904_%E5%B0%8F%E5%9E%8B%E5%AE%A2%E8%BD%A6%E8%BD%A6%E9%81%93.png" alt="小型客车车道.png"></p><h2 id="_12-小型客车车道" tabindex="-1">12. 小型客车车道 <a class="header-anchor" href="#_12-小型客车车道" aria-label="Permalink to &quot;12. 小型客车车道&quot;">​</a></h2><ul><li>logo</li></ul><blockquote><p>两条虚线表示车道，中间是小型客车，表示该车道只供小型客车行驶。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732900365483_%E5%A4%9A%E4%B9%98%E5%91%98%E8%BD%A6%E8%BE%86%EF%BC%88HOV%EF%BC%89%E4%B8%93%E7%94%A8%E8%BD%A6%E9%81%93%E6%A0%87%E5%BF%97.png" alt="多乘员车辆（HOV）专用车道标志.png"></p><h2 id="_13-多乘员车辆-hov-专用车道标志" tabindex="-1">13. 多乘员车辆（HOV）专用车道标志 <a class="header-anchor" href="#_13-多乘员车辆-hov-专用车道标志" aria-label="Permalink to &quot;13. 多乘员车辆（HOV）专用车道标志&quot;">​</a></h2><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732900811293_%E6%B3%A8%E6%84%8F%E6%BD%AE%E6%B1%90%E8%BD%A6%E9%81%93%E8%AD%A6%E5%91%8A%E6%A0%87%E5%BF%97.png" alt="注意潮汐车道警告标志.png"></p><h2 id="_14-注意潮汐车道警告标志" tabindex="-1">14. 注意潮汐车道警告标志 <a class="header-anchor" href="#_14-注意潮汐车道警告标志" aria-label="Permalink to &quot;14. 注意潮汐车道警告标志&quot;">​</a></h2><ul><li>logo</li></ul><blockquote><p>黄牌标志内中间是上下双向箭头，两边的等号像雨水一样落下来，是注意潮汐车道警告标志。</p></blockquote><blockquote><p>潮汐车道就是不同时候方向不一样的车道，根据早晚车流量的不同，行驶方向会发生变化。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1732985409423_%E5%81%9C%E8%BD%A6%E8%AE%A9%E8%A1%8C.png" alt="停车让行.png"></p><h2 id="_15-停车让行" tabindex="-1">15. 停车让行 <a class="header-anchor" href="#_15-停车让行" aria-label="Permalink to &quot;15. 停车让行&quot;">​</a></h2><ul><li>logo</li></ul><blockquote><p>标志形状为正八边形，颜色为红底白字。</p></blockquote><blockquote><p>表示车辆必须在进入路口前完全停止，<code>确认安全后</code>，方可通行。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1733123173811_%E5%81%9C%E8%BD%A6%E5%8C%BA%E9%A2%84%E5%91%8A.png" alt="停车区预告.png"></p><h2 id="_16-停车区预告标志" tabindex="-1">16. 停车区预告标志 <a class="header-anchor" href="#_16-停车区预告标志" aria-label="Permalink to &quot;16. 停车区预告标志&quot;">​</a></h2><ul><li>logo</li></ul><blockquote><p>提供停车以及简单的饮茶服务。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1733127037903_%E9%AB%98%E9%80%9F%E5%85%AC%E8%B7%AF%E7%9B%B4%E5%87%BA%E8%BD%A6%E9%81%93%E6%8C%87%E5%BC%95.png" alt="高速公路直出车道指引.png"></p><h2 id="_17-高速公路直出车道指引" tabindex="-1">17. 高速公路直出车道指引 <a class="header-anchor" href="#_17-高速公路直出车道指引" aria-label="Permalink to &quot;17. 高速公路直出车道指引&quot;">​</a></h2><p>-logo</p><blockquote><p>绿牌高速+黄色车道指引</p></blockquote><blockquote><p>表示该方向无需变更车道，车辆沿当前指引方向可直接驶出高速公路。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1733127719803_Y%E5%BD%A2%E4%BA%A4%E5%8F%89%E8%B7%AF%E5%8F%A3.png" alt="Y形交叉路口.png"></p><h2 id="_18-y形交叉路口" tabindex="-1">18. Y形交叉路口 <a class="header-anchor" href="#_18-y形交叉路口" aria-label="Permalink to &quot;18. Y形交叉路口&quot;">​</a></h2><blockquote><p>用以警告车辆驾驶人前方有交叉路口，注意横向来车。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1733242093400_%E7%A6%81%E6%AD%A2%E6%B3%8A%E8%BD%A6.png" alt="禁止泊车.png"></p><h2 id="_19-禁止泊车" tabindex="-1">19. 禁止泊车 <a class="header-anchor" href="#_19-禁止泊车" aria-label="Permalink to &quot;19. 禁止泊车&quot;">​</a></h2><blockquote><p>表示在限定的范围内，禁止一切车辆<code>临时</code>或<code>长时间</code>停放。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1733242255535_%E7%A6%81%E6%AD%A2%E9%95%BF%E6%97%B6%E9%97%B4%E6%B3%8A%E8%BD%A6.png" alt="禁止长时间泊车.png"></p><h2 id="_20-禁止长时间泊车" tabindex="-1">20. 禁止长时间泊车 <a class="header-anchor" href="#_20-禁止长时间泊车" aria-label="Permalink to &quot;20. 禁止长时间泊车&quot;">​</a></h2><blockquote><p>表示在限定的范围内，禁止一切车辆<code>长时间</code>停放。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1733244399356_%E6%97%A0%E4%BA%BA%E7%9C%8B%E5%AE%88%E7%9A%84%E9%93%81%E8%B7%AF%E9%81%93%E5%8F%A3.png" alt="无人看守的铁路道口.png"></p><h2 id="_21-无人看守的铁路道口" tabindex="-1">21. 无人看守的铁路道口 <a class="header-anchor" href="#_21-无人看守的铁路道口" aria-label="Permalink to &quot;21. 无人看守的铁路道口&quot;">​</a></h2><blockquote><p>用以警告车辆驾驶人注意前方有铁路道口，应减速慢行或及时停车。“一停、二看、三通过”。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1733301269716_%E4%BC%9A%E8%BD%A6%E8%AE%A9%E8%A1%8C.png" alt="会车让行.png"></p><h2 id="_22-会车让行" tabindex="-1">22. 会车让行 <a class="header-anchor" href="#_22-会车让行" aria-label="Permalink to &quot;22. 会车让行&quot;">​</a></h2><blockquote><p>会车让行，表示双向车辆在会车时对方享有优先通行权利。箭头小的一方需要让行，我方的箭头小，是会车让行。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1733417488993_%E7%8E%AF%E5%BD%A2%E4%BA%A4%E5%8F%89%E8%B7%AF%E5%8F%A3.png" alt="环形交叉路口.png"></p><h2 id="_23-环形交叉路口" tabindex="-1">23. 环形交叉路口 <a class="header-anchor" href="#_23-环形交叉路口" aria-label="Permalink to &quot;23. 环形交叉路口&quot;">​</a></h2><ul><li>可以掉头</li></ul><blockquote><p>注意前方是环形交叉路口，在环形交叉路口是可以掉头的。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1752135168712_%E5%89%8D%E6%96%B9%E8%B7%AF%E5%8F%A3%E5%8F%AA%E5%85%81%E8%AE%B8%E7%9B%B4%E8%A1%8C.png" alt="前方路口只允许直行.png"></p><h2 id="_24-禁止向左和向右转弯标志" tabindex="-1">24. 禁止向左和向右转弯标志 <a class="header-anchor" href="#_24-禁止向左和向右转弯标志" aria-label="Permalink to &quot;24. 禁止向左和向右转弯标志&quot;">​</a></h2><ul><li>只允许直行</li></ul><blockquote><p>表示前方路口禁止一切车辆向左向右转弯。 红圈禁令，箭头指示左转和右转，合起来就是禁止向左向右转弯。左右方向箭头被禁止，只能直行。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1752138251304_%E7%A6%81%E6%AD%A2%E9%A9%B6%E5%85%A5.png" alt="禁止驶入.png"></p><h2 id="_25-禁止驶入" tabindex="-1">25. 禁止驶入 <a class="header-anchor" href="#_25-禁止驶入" aria-label="Permalink to &quot;25. 禁止驶入&quot;">​</a></h2><blockquote><p>表示禁止一切车辆驶入（含非机动车）。</p></blockquote><h2 id="_26-最低限速标志" tabindex="-1">26. 最低限速标志 <a class="header-anchor" href="#_26-最低限速标志" aria-label="Permalink to &quot;26. 最低限速标志&quot;">​</a></h2><blockquote><p>蓝底白边白字，加低水平的白底，附上数字。</p></blockquote><h2 id="_27-驼峰桥" tabindex="-1">27. 驼峰桥 <a class="header-anchor" href="#_27-驼峰桥" aria-label="Permalink to &quot;27. 驼峰桥&quot;">​</a></h2><blockquote><p>中间凸起像个桥，是驼峰桥。 两个凸起，是路面不平。 中间一个凸起，是减速丘。 中间向下凹，是路面低洼。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1752143724461_%E5%89%8D%E6%96%B9%E8%B7%AF%E5%8F%A3%E4%B8%8D%E8%83%BD%E7%9B%B4%E8%A1%8C.png" alt="前方路口不能直行.png"></p><h2 id="_28-前方路口不能直行-交叉路口标志" tabindex="-1">28. 前方路口不能直行，交叉路口标志 <a class="header-anchor" href="#_28-前方路口不能直行-交叉路口标志" aria-label="Permalink to &quot;28. 前方路口不能直行，交叉路口标志&quot;">​</a></h2><blockquote><p>黄色三角警告标志内见到一个“T”字，是T形交叉路口警告标志，表示前方路段只有左右方向岔路，不可直行！</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1752314632666_%E5%82%8D%E5%B1%B1%E9%99%A9%E8%B7%AF.png" alt="傍山险路.png"></p><h2 id="_29-傍山险路" tabindex="-1">29. 傍山险路 <a class="header-anchor" href="#_29-傍山险路" aria-label="Permalink to &quot;29. 傍山险路&quot;">​</a></h2><blockquote><p>用以提醒车辆驾驶人谨慎驾驶，注意路侧危险。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1752314628810_%E6%B3%A8%E6%84%8F%E8%90%BD%E7%9F%B3.png" alt="注意落石.png"></p><h2 id="_30-注意落石" tabindex="-1">30. 注意落石 <a class="header-anchor" href="#_30-注意落石" aria-label="Permalink to &quot;30. 注意落石&quot;">​</a></h2><blockquote><p>用以提醒车辆驾驶人谨慎驾驶，注意落石。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1752314933633_%E5%B7%A6%E5%8F%B3%E7%BB%95%E8%A1%8C.png" alt="左右绕行.png"></p><h2 id="_31-左右绕行" tabindex="-1">31. 左右绕行 <a class="header-anchor" href="#_31-左右绕行" aria-label="Permalink to &quot;31. 左右绕行&quot;">​</a></h2><blockquote><p>用以提醒前方道路有障碍物，车辆驾驶人应左右绕行，谨慎驾驶。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1752315858883_%E7%AA%84%E6%A1%A5.png" alt="窄桥.png"></p><h2 id="_32-窄桥" tabindex="-1">32. 窄桥 <a class="header-anchor" href="#_32-窄桥" aria-label="Permalink to &quot;32. 窄桥&quot;">​</a></h2><blockquote><p>用以警告车辆驾驶人注意前方桥面宽度变窄。</p></blockquote><p><img src="https://cloudflare-imgbed-7oz.pages.dev/file/1752317053945_%E4%BA%8B%E6%95%85%E6%98%93%E5%8F%91%E8%B7%AF%E6%AE%B5%E6%A0%87%E5%BF%97.png" alt="事故易发路段标志.png"></p><h2 id="_33-事故易发路段标志" tabindex="-1">33. 事故易发路段标志 <a class="header-anchor" href="#_33-事故易发路段标志" aria-label="Permalink to &quot;33. 事故易发路段标志&quot;">​</a></h2><blockquote><p>用以警告前方道路为事故易发路段，车辆驾驶人应谨慎驾驶。</p></blockquote>',138)]))}const n=o(p,[["render",i]]);export{s as __pageData,n as default};
