import{_ as a,c as o,a3 as t,o as l}from"./chunks/framework.CAwIQbWu.js";const p=JSON.parse('{"title":"责任篇","description":"","frontmatter":{},"headers":[],"relativePath":"SubjectOne/常识/责任篇.md","filePath":"SubjectOne/常识/责任篇.md","lastUpdated":1752144059000}'),c={name:"SubjectOne/常识/责任篇.md"};function r(u,e,i,n,d,b){return l(),o("div",null,e[0]||(e[0]=[t('<h1 id="责任篇" tabindex="-1">责任篇 <a class="header-anchor" href="#责任篇" aria-label="Permalink to &quot;责任篇&quot;">​</a></h1><h2 id="_1-没有过错10" tabindex="-1">1. 没有过错10% <a class="header-anchor" href="#_1-没有过错10" aria-label="Permalink to &quot;1. 没有过错10%&quot;">​</a></h2><blockquote><p>驾驶机动车与行人发生交通事故，造成人身伤亡、财产损失的，机动车无过错要承担<code>不超过10%</code>的赔偿责任。</p></blockquote><h2 id="_2-调10罚15事3" tabindex="-1">2. 调10罚15事3 <a class="header-anchor" href="#_2-调10罚15事3" aria-label="Permalink to &quot;2. 调10罚15事3&quot;">​</a></h2><ul><li>提出书面调解申请（10日内）</li></ul><blockquote><p>当事人对交通事故损害赔偿有争议，请求公安机关交通管理部门调解，在收到交通事故认定书之日起<code>10日内</code>要提出书面调节申请。</p></blockquote><ul><li>终止调解</li></ul><blockquote><p>对事故赔偿有争议的，当事人若在调解期间向法院提起民事诉讼，调解终止。</p></blockquote><h2 id="_3-对方也有错" tabindex="-1">3. 对方也有错 <a class="header-anchor" href="#_3-对方也有错" aria-label="Permalink to &quot;3. 对方也有错&quot;">​</a></h2><ul><li>部分责任</li></ul><blockquote><p>造成交通事故后当事人逃逸的，且有证据证明对方当事人也有过错的，逃逸的当事人无需承担全部责任。</p></blockquote><blockquote><p>发生事故后逃逸的负全责，若对方也有过错，则会进行责任划分。</p></blockquote><h2 id="_4-静止无责-动10" tabindex="-1">4. 静止无责，动10 <a class="header-anchor" href="#_4-静止无责-动10" aria-label="Permalink to &quot;4. 静止无责，动10&quot;">​</a></h2><blockquote><p>发生事故时，机动车处于静止状态，所以不用承担责任。</p></blockquote><ul><li>承担10%赔偿责任</li></ul><blockquote><p>若机动车不是静止状态，即使没有责任，也有可能要承担不超过10%的责任。</p></blockquote><h2 id="_5-学驾时违法" tabindex="-1">5. 学驾时违法 <a class="header-anchor" href="#_5-学驾时违法" aria-label="Permalink to &quot;5. 学驾时违法&quot;">​</a></h2><ul><li>教练员承担责任</li></ul><blockquote><p>学车过程中，是教练员直接指导学员的驾车操作，发生交通事故由教练员担责。无论学员是否有过错，教练员都应当对学员的违法行为承担责任。</p></blockquote><blockquote><p>学员在学习过程中发生的交通事故中由教练员或随车指导人员承担主要责任。</p></blockquote>',20)]))}const q=a(c,[["render",r]]);export{p as __pageData,q as default};
