import{_ as e,c as l,a3 as a,o as t}from"./chunks/framework.CAwIQbWu.js";const h=JSON.parse('{"title":"记12分题","description":"","frontmatter":{},"headers":[],"relativePath":"SubjectOne/记分题/记12分题.md","filePath":"SubjectOne/记分题/记12分题.md","lastUpdated":1752144059000}'),r={name:"SubjectOne/记分题/记12分题.md"};function i(c,o,u,d,n,p){return t(),l("div",null,o[0]||(o[0]=[a('<h1 id="记12分题" tabindex="-1">记12分题 <a class="header-anchor" href="#记12分题" aria-label="Permalink to &quot;记12分题&quot;">​</a></h1><blockquote><p><strong>普通倒1逆3，高速倒逆12。</strong></p></blockquote><blockquote><p>扣分口诀：违3遮9假12</p></blockquote><blockquote><p>替罪12</p></blockquote><h2 id="_1-超速50" tabindex="-1">1. 超速50% <a class="header-anchor" href="#_1-超速50" aria-label="Permalink to &quot;1. 超速50%&quot;">​</a></h2><ul><li><p><strong>记12分</strong></p></li><li><p>罚款200-2000元</p></li><li><p>吊销驾驶证</p></li></ul><blockquote><p>驾驶小型汽车在<code>高速公路</code>、<code>城市快速路</code>上行驶超过规定时速50%以上的，一次记12分。</p></blockquote><h2 id="_2-高速倒逆12" tabindex="-1">2. 高速倒逆12 <a class="header-anchor" href="#_2-高速倒逆12" aria-label="Permalink to &quot;2. 高速倒逆12&quot;">​</a></h2><ul><li><strong>记12分</strong></li></ul><blockquote><p>驾驶机动车在<code>高速公路</code>、<code>城市快速路</code>上倒车、逆行、穿越中央分隔带掉头的，一次记12分。</p></blockquote><h2 id="_3-替罪" tabindex="-1">3. 替罪 <a class="header-anchor" href="#_3-替罪" aria-label="Permalink to &quot;3. 替罪&quot;">​</a></h2><ul><li><strong>记12分</strong></li></ul><blockquote><p>代替实际机动车驾驶人接受交通违法行为处罚和记分牟取经济利益的，一次记12分。</p></blockquote><h2 id="_4-超载100" tabindex="-1">4. 超载100% <a class="header-anchor" href="#_4-超载100" aria-label="Permalink to &quot;4. 超载100%&quot;">​</a></h2><ul><li><strong>记12分</strong></li></ul><blockquote><p>驾驶<code>小型载客汽车</code>超过核定人数100%以上的，一次记12分。</p></blockquote><h2 id="_5-超载-20-50" tabindex="-1">5. 超载[20%-50%) <a class="header-anchor" href="#_5-超载-20-50" aria-label="Permalink to &quot;5. 超载[20%-50%)&quot;">​</a></h2><ul><li><strong>记12分</strong></li></ul><blockquote><p>驾驶<code>校车</code>、<code>公路客运汽车</code>、<code>旅游客运汽车</code>载人超过核定人数20%未达50%的，一次记12分。</p></blockquote><h2 id="_6-逃逸-轻伤" tabindex="-1">6. 逃逸（轻伤） <a class="header-anchor" href="#_6-逃逸-轻伤" aria-label="Permalink to &quot;6. 逃逸（轻伤）&quot;">​</a></h2><ul><li><strong>记12分</strong></li><li>处15日拘留</li><li>罚款200-2000元</li></ul><blockquote><p>机动车驾驶人造成致人轻伤以上或者死亡的交通事故后逃逸，<code>尚不构成犯罪的</code>，一次记12分。</p></blockquote><blockquote><p>凡是肇事逃逸者，吊销驾驶证，且终生不得重新申领。所以这里不是<code>肇事逃逸者</code>。</p></blockquote><blockquote><p>凡是逃逸的（<code>构成犯罪的</code>），终生不得重新申领驾驶证。</p></blockquote><blockquote><p>造成交通事故后逃逸，尚不构成犯罪的，处15日拘留，并处罚款200-2000元。</p></blockquote><h2 id="_7-虚假号牌-假12" tabindex="-1">7. 虚假号牌（假12） <a class="header-anchor" href="#_7-虚假号牌-假12" aria-label="Permalink to &quot;7. 虚假号牌（假12）&quot;">​</a></h2><ul><li><strong>记12分</strong></li></ul><blockquote><p>使用其他机动车号牌、行驶证的，将一次记12分。</p></blockquote><h2 id="_8-初次饮酒后驾车" tabindex="-1">8. 初次饮酒后驾车 <a class="header-anchor" href="#_8-初次饮酒后驾车" aria-label="Permalink to &quot;8. 初次饮酒后驾车&quot;">​</a></h2><ul><li><p>暂扣驾驶证6个月</p></li><li><p>罚款1000-2000元</p></li><li><p><strong>记12分</strong></p></li></ul><h2 id="_9-再次饮酒后驾车" tabindex="-1">9. 再次饮酒后驾车 <a class="header-anchor" href="#_9-再次饮酒后驾车" aria-label="Permalink to &quot;9. 再次饮酒后驾车&quot;">​</a></h2><ul><li><p>处10日以下拘留</p></li><li><p>罚款1000-2000元</p></li><li><p>吊销驾驶证</p></li><li><p><strong>记12分</strong></p></li></ul><h2 id="_10-校车超速-20-含-高速公路" tabindex="-1">10. 校车超速 20%(含)，高速公路 <a class="header-anchor" href="#_10-校车超速-20-含-高速公路" aria-label="Permalink to &quot;10. 校车超速 20%(含)，高速公路&quot;">​</a></h2><ul><li><p><strong>记12分</strong></p></li><li><p>罚款200元</p></li></ul><blockquote><p>驾驶<code>校车</code>在<code>高速公路</code>、<code>城市快速路</code>的道路上行驶超过规定时速百分之20(含)以上，一次记12分。</p></blockquote>',35)]))}const b=e(r,[["render",i]]);export{h as __pageData,b as default};
