import{_ as e,c as a,a3 as t,o as l}from"./chunks/framework.CAwIQbWu.js";const b=JSON.parse('{"title":"记9分题","description":"","frontmatter":{},"headers":[],"relativePath":"SubjectOne/记分题/记9分题.md","filePath":"SubjectOne/记分题/记9分题.md","lastUpdated":1752144059000}'),r={name:"SubjectOne/记分题/记9分题.md"};function i(n,o,c,u,s,d){return l(),a("div",null,o[0]||(o[0]=[t('<h1 id="记9分题" tabindex="-1">记9分题 <a class="header-anchor" href="#记9分题" aria-label="Permalink to &quot;记9分题&quot;">​</a></h1><blockquote><p>违3遮9假12</p></blockquote><h2 id="q1-机动车驾驶人存在以下哪种行为将被一次记9分" tabindex="-1">Q1：机动车驾驶人存在以下哪种行为将被一次记9分？ <a class="header-anchor" href="#q1-机动车驾驶人存在以下哪种行为将被一次记9分" aria-label="Permalink to &quot;Q1：机动车驾驶人存在以下哪种行为将被一次记9分？&quot;">​</a></h2><p>A：驾驶机动车不按规定避让校车的【不让校车】（3）<br> B：驾驶证被暂扣期间驾驶机动车的【暂扣开车】（6）<br> C：驾驶机动车不按交通信号灯指示通行的【闯红灯】（6）<br><strong><u>D</u></strong>：驾驶与准驾车型不符的机动车的【准驾不符】（9）</p><p>Answer：<strong><u>D</u></strong></p><h2 id="_1-遮9" tabindex="-1">1. 遮9 <a class="header-anchor" href="#_1-遮9" aria-label="Permalink to &quot;1. 遮9&quot;">​</a></h2><ul><li><strong>记9分</strong></li></ul><blockquote><p>驾驶故意遮挡、污损号牌的机动车上道路行驶的，一次记9分。</p></blockquote><blockquote><p>机动车<code>未悬挂车号牌</code>，将一次记9分。（...全遮）</p></blockquote><h2 id="_2-准驾不符" tabindex="-1">2. 准驾不符 <a class="header-anchor" href="#_2-准驾不符" aria-label="Permalink to &quot;2. 准驾不符&quot;">​</a></h2><ul><li><strong>记9分</strong></li></ul><blockquote><p>驾驶与准驾车型不符的机动车，一次记9分。</p></blockquote><h2 id="_3-超载-50-100" tabindex="-1">3. 超载[50%-100%) <a class="header-anchor" href="#_3-超载-50-100" aria-label="Permalink to &quot;3. 超载[50%-100%)&quot;">​</a></h2><ul><li><strong>记9分</strong></li></ul><blockquote><p>驾驶<code>7座以上载客汽车</code>载人超过核定人数50%以上未达到100%的，一次记9分。</p></blockquote><h2 id="_4-违停" tabindex="-1">4. 违停 <a class="header-anchor" href="#_4-违停" aria-label="Permalink to &quot;4. 违停&quot;">​</a></h2><ul><li><strong>记9分</strong></li></ul><blockquote><p>在<code>高速公路</code>或<code>城市快速路</code>上违法停车的，一次记9分。</p></blockquote><h2 id="_5-疲劳驾驶-客9货3" tabindex="-1">5. 疲劳驾驶（客9货3） <a class="header-anchor" href="#_5-疲劳驾驶-客9货3" aria-label="Permalink to &quot;5. 疲劳驾驶（客9货3）&quot;">​</a></h2><ul><li><strong>记9分</strong></li></ul><blockquote><p>（&gt;4小时或&lt;20分钟）中型以上载客汽车</p></blockquote><blockquote><p>连续驾驶中型以上<strong>载客汽车</strong>、危险物品运输车辆超过4小时未停车休息或者停车休息时间少于20分钟的，一次记9分。</p></blockquote><h2 id="_6-未悬挂号牌-包括临时行驶车号牌已过期" tabindex="-1">6. 未悬挂号牌（包括临时行驶车号牌已过期） <a class="header-anchor" href="#_6-未悬挂号牌-包括临时行驶车号牌已过期" aria-label="Permalink to &quot;6. 未悬挂号牌（包括临时行驶车号牌已过期）&quot;">​</a></h2><ul><li><p>处200元罚款</p></li><li><p><strong>记9分</strong></p></li><li><p>扣车</p></li></ul><h2 id="_7-校车超速-50-含" tabindex="-1">7. 校车超速 &gt; 50%(含) <a class="header-anchor" href="#_7-校车超速-50-含" aria-label="Permalink to &quot;7. 校车超速 &gt; 50%(含)&quot;">​</a></h2><ul><li><p><strong>记9分</strong></p></li><li><p>罚款200元</p></li></ul><blockquote><p>驾驶<code>校车</code>在<code>高速公路</code>、<code>城市快速路</code>以外的道路上行驶超过规定时速百分之50(含)以上的，一次记9分。</p></blockquote><h2 id="_8-未取得校车驾驶资格驾驶校车" tabindex="-1">8. 未取得校车驾驶资格驾驶校车 <a class="header-anchor" href="#_8-未取得校车驾驶资格驾驶校车" aria-label="Permalink to &quot;8. 未取得校车驾驶资格驾驶校车&quot;">​</a></h2><ul><li><strong>记9分</strong></li></ul>',29)]))}const p=e(r,[["render",i]]);export{b as __pageData,p as default};
